    const [movies, setMovies] = useState([])

    //make request reusable--remove hardcoding from useEffect and move to its own function 
    //GET ALL 
    function getMovies() {
        axios.get("/movies")
            // .then(res => console.log(res)) //test 
            .then(res => setMovies(res.data))
            .catch(err => console.log(err))
    }

